// Extract workflow from request body
const workflow = $input.first().json.body;

// Strip sensitive data
const sanitized = {
  name: workflow.name,
  nodes: workflow.nodes?.map(node => {
    const { credentials, ...rest } = node;
    return rest;
  }) || [],
  connections: workflow.connections || {},
  settings: workflow.settings || {},
  // Add metadata
  _metadata: {
    exportedAt: new Date().toISOString(),
    exportedFrom: 'n8n-self-hosted',
    originalId: workflow.id
  }
};

// Generate filename from workflow name
const filename = workflow.name
  .toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/^-|-$/g, '') + '.json';

return {
  json: {
    filename,
    content: JSON.stringify(sanitized, null, 2),
    workflowName: workflow.name,
    message: `Export workflow: ${workflow.name}`
  }
};

